function({series, seriesIndex, dataPointIndex, w}) {
    let htmlContent = '<div class="arrow_box" style="'
        + 'background-color: #f9f9f9; padding: 10px; border: 1px solid #ddd; '
        + 'box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.2); border-radius: 5px; '
        + 'font-family: Arial, sans-serif; font-size: 14px; color: #333; '
        + 'line-height: 1; text-align: left;">';

    const formatValue = value => Math.round(value).toString();

    // Added margin-right to the span for spacing
    htmlContent += '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">Maximum:</span><b>' + formatValue(w.globals.seriesCandleC[0][dataPointIndex]) + '</b></div>'
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">75%:</span><b>' + formatValue(w.globals.seriesCandleL[0][dataPointIndex]) + '</b></div>'
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">Median:</span><b>' + formatValue(w.globals.seriesCandleM[0][dataPointIndex]) + '</b></div>'
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">25%:</span><b>' + formatValue(w.globals.seriesCandleH[0][dataPointIndex]) + '</b></div>'
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">Minimum:</span><b>' + formatValue(w.globals.seriesCandleO[0][dataPointIndex]) + '</b></div>'
                 + '<br>' // Line break before Count
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">Count:</span><b>' + formatValue(series[1][dataPointIndex]) + '</b></div>'
				 + '<br>' // Line break before SP
                 + '<div style="display: flex; justify-content: space-between;"><span style="margin-right: 10px;">SP:</span><b>' + formatValue(series[2][dataPointIndex]) + '</b></div>'
                 + '</div>';

    return htmlContent;
}

